function ibacor_google_stumbleupon(d) {
    $.ajax({
        url: 'https://count.donreach.com/?url=' + d,
        crossDomain: true,
        dataType: 'jsonp'
    }).done(function(a) {
        var b = a.shares.google,
            htmlupon = '';
        if (a.shares.stumbleupon != null) {
            htmlupon += a.shares.stumbleupon
        } else {
            htmlupon += 0
        }
        $('#ibacor_count_gp').html(b);
        $('#ibacor_count_up').html(htmlupon);
        var c = parseInt(b) + parseInt(htmlupon);
        ibacor_buffer(d, c)
    }).fail(function() {
        $('#ibacor_count_gp').html('0');
        $('#ibacor_count_up').html('0');
        ibacor_buffer(d, 0)
    })
}

function ibacor_buffer(d, e) {
    $.ajax({
        url: 'https://api.bufferapp.com/1/links/shares.json?url=' + d,
        crossDomain: true,
        dataType: 'jsonp'
    }).done(function(a) {
        var b = a.shares;
        $('#ibacor_buffer').html(b);
        var c = parseInt(b) + parseInt(e);
        ibacor_facebook(d, c)
    })
}

function ibacor_facebook(d, e) {
    $.ajax({
        url: 'https://api.facebook.com/method/links.getStats?urls=' + d + '&format=json',
        crossDomain: true,
        dataType: 'jsonp'
    }).done(function(a) {
        var b = a[0].total_count;
        $('#ibacor_count_fb').html(b);
        var c = parseInt(b) + parseInt(e);
        ibacor_twitter(d, c)
    })
}

function ibacor_twitter(d, e) {
    $.ajax({
        url: 'http://cdn.api.twitter.com/1/urls/count.json?url=' + d,
        crossDomain: true,
        dataType: 'jsonp'
    }).done(function(a) {
        var b = a.count;
        $('#ibacor_count_tw').html(b);
        var c = parseInt(b) + parseInt(e);
        ibacor_linkedin(d, c)
    }).fail(function() {
        $('#ibacor_count_tw').html('0');
        ibacor_linkedin(d, e)
    })
}

function ibacor_linkedin(d, e) {
    $.ajax({
        url: 'https://www.linkedin.com/countserv/count/share?url=' + d + '&callback=?',
        crossDomain: true,
        dataType: 'json'
    }).done(function(a) {
        var b = a.count;
        $('#ibacor_count_in').html(b);
        var c = parseInt(b) + parseInt(e);
        ibacor_pinterest(d, c)
    })
}

function ibacor_pinterest(d, e) {
    $.ajax({
        url: 'http://api.pinterest.com/v1/urls/count.json?url=' + d + '&callback=?',
        crossDomain: true,
        dataType: 'json'
    }).done(function(a) {
        var b = a.count;
        $('#ibacor_count_pn').html(b);
        var c = parseInt(b) + parseInt(e);
        ibacor_reddit(d, c)
    })
}

function ibacor_reddit(d, e) {
    $.ajax({
        url: 'http://www.reddit.com/api/info.json?url=' + d,
        crossDomain: true,
        dataType: 'json'
    }).done(function(a) {
        var b;
        if (a.data.children != '') {
            b = a.data.children[0].data.score
        } else {
            b = 0
        }
        $('#ibacor_count_rd').html(b);
        var c = parseInt(b) + parseInt(e);
        $('#ibacor_count_sh').html(c)
    }).fail(function() {
        $('#ibacor_count_rd').html('0');
        $('#ibacor_count_sh').html(e)
    })
}
